const Content = styled.div`
    .sidebar>li{
        border-top: 0.6px solid;
        line-height: 40px;
        font-weight: 700;
        white-space: normal;
    }
    .sidebar>li:last-child{
        border-bottom: 0.6px solid;
    }
    .submenu{
        max-height: 0px;
        overflow: hidden;
        transition: all 1s  ease;
        display: block;
    }
    .menu{
        font-size:12px;
        line-height: 26px;
        font-weight: 100;
        
    }
    .arrow {
        display: inline;
        float: right;
        margin-top: 15px;
        width: 11px;
        height: 11px;
        border-top: 2px solid #ccc;
        border-left: 2px solid #ccc;
        transform: rotate(135deg);
        transition: transform 1s  ease;
    }
    .arrowRotate{
        transform: rotate(225deg);
    }
    .filterBtn{
        width: 116px;
        line-height: 2;
        border: 1px solid #5b5b5b;
        padding: 0 15px;
        border-radius: 0;
        float: left;
    }
    .filterBtn::before{
        content:"";
        display: inline;
        float: right;
        margin-top: 8px;
        width: 8px;
        height: 8px;
        border-top: 3px solid #ccc;
        border-left: 3px solid #ccc;
        transform: rotate(135deg);
        transition: transform 1s  ease;
    }
    .filterBtn:focus::before, .filterBtn:active::before{
        content:"";
        transform: rotate(225deg);
    }
    .filterBtn:focus+div, .filterBtn:active+div{
        visibility: visible;
    }
    .filterAction{
        height: 96px;
        width: 116px;
        position: absolute;
        top: 24px;
        line-height: 2;
        border: 1px solid #5b5b5b;
        border-top: 0px;
        border-radius: 0;
        float: left;
        z-index: 9;
        visibility: hidden;
        transition: visibility 0.3s;
    }
    
    .filterAction>div{
        padding: 0 12px;
        background-color: white;
    }
    .filterAction>div:hover{
        background-color: #5b5b5b;
        color: white;
    }
    .square-sm{
        background-color: #f3f3f3;
        width: 10px;
        height: 13px;
        margin-left: 1px;
    }
    .ms-15px{
        margin-left: 15px;
    }
    .bg-grey>div{
        background-color: #868686;
    }
    .separate{
        width: 1px;
        border: 0.5px solid #868686;
    }
    .square-m{
        display: inline-block;
        width: 16px;
        height: 16px;
        margin: 0 1px;
    }
    .price>span{
        color: #868686;
        padding: 1px 0;
        line-height: 20px;
        font-weight: 400;
    }
    .imgAction>a:focus, .imgAction>a:active{
        box-shadow: 0px 0px 0px 1.5px rgba(0,0,0, .5);
    }
`
